body.script:
// FUNCIÓN PRINCIPAL PARA BLOGGER - SIN CONFLICTOS
function initBloggerInteractions() {
    // === MENÚ MÓVIL ===
    const mobileMenuButton = document.getElementById('mobile-menu-button');
    const mobileMenu = document.getElementById('mobile-menu');
    const iconOpen = document.getElementById('icon-open');
    const iconClose = document.getElementById('icon-close');

    if (mobileMenuButton && mobileMenu) {
        mobileMenuButton.addEventListener('click', function(e) {
            e.preventDefault();
            e.stopPropagation();

            const isHidden = mobileMenu.classList.contains('hidden');

            if (isHidden) {
                mobileMenu.classList.remove('hidden');
                iconOpen.classList.add('hidden');
                iconClose.classList.remove('hidden');
            } else {
                mobileMenu.classList.add('hidden');
                iconOpen.classList.remove('hidden');
                iconClose.classList.add('hidden');
            }
        });
    }

    // === ACORDEÓN MEJORADO ===
    const accordionContainer = document.getElementById('accordion-container');
    if (accordionContainer) {
        const videoDisplayArea = document.getElementById('video-display-area');
        const accordionItems = accordionContainer.querySelectorAll('.accordion-item');
        const videoContainers = videoDisplayArea.querySelectorAll('.video-container');

        // Usar event delegation para evitar conflictos
        accordionContainer.addEventListener('click', function(e) {
            e.preventDefault();

            const header = e.target.closest('.accordion-header');
            if (!header) return;

            const currentItem = header.closest('.accordion-item');
            const targetVideoId = currentItem.getAttribute('data-target');

            // Cerrar todos los acordeones excepto el actual
            accordionItems.forEach(function(item) {
                if (item === currentItem) {
                    item.classList.toggle('active');
                } else {
                    item.classList.remove('active');
                }
            });

            // Mostrar/ocultar videos correspondientes
            videoContainers.forEach(function(video) {
                if (video.id === targetVideoId && currentItem.classList.contains('active')) {
                    video.classList.remove('hidden');
                } else {
                    video.classList.add('hidden');
                }
            });
        });
    }

    // === SISTEMA DE TABS MEJORADO ===
    const tabsContainer = document.getElementById('tabs-container');
    if (tabsContainer) {
        const tabButtons = tabsContainer.querySelectorAll('.tab-button');
        const tabPanels = tabsContainer.querySelectorAll('.tab-panel');

        tabButtons.forEach(function(button) {
            button.addEventListener('click', function(e) {
                e.preventDefault();

                const targetPanelId = this.getAttribute('data-target');

                // Actualizar botones
                tabButtons.forEach(function(btn) {
                    btn.classList.remove('active');
                });
                this.classList.add('active');

                // Actualizar paneles
                tabPanels.forEach(function(panel) {
                    if (panel.id === targetPanelId) {
                        panel.classList.remove('hidden');
                        panel.classList.add('grid');
                    } else {
                        panel.classList.add('hidden');
                        panel.classList.remove('grid');
                    }
                });
            });
        });
    }

    // === VIDEO INTERACTIVO CON VIMEO ===
    const videoIframe = document.getElementById('hero-video');
    if (videoIframe && window.Vimeo) {
        try {
            const player = new Vimeo.Player(videoIframe);
            const overlay = document.getElementById('video-overlay');

            if (overlay) {
                overlay.addEventListener('click', function() {
                    player.setMuted(false);
                    player.setVolume(0.7);
                    player.play();
                    overlay.style.opacity = '0';
                    player.setControls(true);
                });

                player.on('pause', function() {
                    overlay.style.opacity = '1';
                });

                player.on('ended', function() {
                    overlay.style.opacity = '1';
                });
            }
        } catch (e) {
            console.warn("Vimeo Player no disponible:", e);
        }
    }
}

// === INICIALIZACIÓN SEGURA ===
function safeInit() {
    if (document.readyState === 'loading') {
        document.addEventListener('DOMContentLoaded', initBloggerInteractions);
    } else {
        initBloggerInteractions();
    }
}

// Ejecutar cuando esté listo
safeInit();
